mapper.delete:
DELETE
FROM
    tbl_reserve
WHERE
    person_code = #{personCode}
and hospital_code = #{hospitalCode}

mapper.update:
UPDATE tbl_reserve
SET
    reserve_time = #{reserveTime},
    reserve_date = #{reserveDate},
    reserve_text = #{reserveText},
    reserve_information_collection = #{reserveInformationCollection}
WHERE
person_code = #{personCode}
and hospital_code = #{hospitalCode}
